/**<br>*Java6原生支持JavaScript，这个例子测试了Java调用脚本（使用脚本引擎和Java类）和脚本调用Java<br>*/<br><br>import javax.script.*;<br><br>public class HelloScriptWorld{<br>&nbsp;&nbsp; &nbsp;public static void main(String args[]) throws ScriptException, NoSuchMethodException {<br>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;ScriptEngineManager scriptEngineMgr = new ScriptEngineManager();<br>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;ScriptEngine jsEngine = scriptEngineMgr.getEngineByName("JavaScript");<br>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;if (jsEngine == null) {<br>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;System.err.println("No script engine found for JavaScript");<br>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;System.exit(1);<br>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;}<br>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;System.out.println("Calling invokeHelloScript...");<br>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;invokeHelloScript(jsEngine);<br><br>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;System.out.println("
Calling defineScriptFunction...");<br>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;defineScriptFunction(jsEngine);<br><br>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;System.out.println("
Calling invokeScriptFunctionFromEngine...");<br>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;invokeScriptFunctionFromEngine(jsEngine);<br><br>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;System.out.println("
Calling invokeScriptFunctionFromJava...");<br>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;invokeScriptFunctionFromJava(jsEngine);<br><br>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;System.out.println("
Calling invokeJavaFromScriptFunction...");<br>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;invokeJavaFromScriptFunction(jsEngine);<br>&nbsp;&nbsp; &nbsp;}<br>&nbsp;&nbsp; &nbsp;private static void invokeHelloScript(ScriptEngine jsEngine) throws ScriptException {<br>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;jsEngine.eval("println('Hello from JavaScript')");<br>&nbsp;&nbsp; &nbsp;}<br>&nbsp;&nbsp; &nbsp;private static void defineScriptFunction(ScriptEngine engine) throws ScriptException {<br>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;// Define a function in the script engine<br>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;engine.eval(<br>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;"function sayHello(name) {" +<br>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;" println('Hello, ' + name);" +<br>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;"}"<br>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;);<br>&nbsp;&nbsp; &nbsp;}<br>&nbsp;&nbsp; &nbsp;private static void invokeScriptFunctionFromEngine(ScriptEngine engine)throws ScriptException{<br>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;engine.eval("sayHello('World!')");<br>&nbsp;&nbsp; &nbsp;}<br>&nbsp;&nbsp; &nbsp;private static void invokeScriptFunctionFromJava(ScriptEngine engine)throws ScriptException, NoSuchMethodException{<br>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;Invocable invocableEngine = (Invocable) engine;<br>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;invocableEngine.invokeFunction("sayHello", "from Java");<br>&nbsp;&nbsp; &nbsp;}<br>&nbsp;&nbsp; &nbsp;private static void invokeJavaFromScriptFunction(ScriptEngine engine)throws ScriptException&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;{<br>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;engine.put("helloScriptWorld", new HelloScriptWorld());<br>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;engine.eval(<br>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;"println('Invoking getHelloReply method from JavaScript...');" +<br>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;"var msg = he