////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////<br>#ifndef _ArcBall_h<br>#define _ArcBall_h<br><br>#include &lt;stdlib.h&gt;<br><br>// 仅在Debug模式下，启用断言<br>#ifdef _DEBUG<br># include "assert.h"<br>#else<br># define assert(x) { }<br>#endif<br><br><br>//2维点<br>typedef union Tuple2f_t<br>{<br>&nbsp;&nbsp;&nbsp; struct<br>&nbsp;&nbsp;&nbsp; {<br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; GLfloat X, Y;<br>&nbsp;&nbsp;&nbsp; } s;<br><br>&nbsp;&nbsp;&nbsp; GLfloat T[2];<br>} Tuple2fT;&nbsp;&nbsp;&nbsp;&nbsp; <br><br>//3维点<br>typedef union Tuple3f_t<br>{<br>&nbsp;&nbsp;&nbsp; struct<br>&nbsp;&nbsp;&nbsp; {<br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; GLfloat X, Y, Z;<br>&nbsp;&nbsp;&nbsp; } s;<br><br>&nbsp;&nbsp;&nbsp; GLfloat T[3];<br>} Tuple3fT;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <br><br>//4维点<br>typedef union Tuple4f_t<br>{<br>&nbsp;&nbsp;&nbsp; struct<br>&nbsp;&nbsp;&nbsp; {<br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; GLfloat X, Y, Z, W;<br>&nbsp;&nbsp;&nbsp; } s;<br><br>&nbsp;&nbsp;&nbsp; GLfloat T[4];<br>} Tuple4fT;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <br><br><br>//3x3矩阵<br>typedef union Matrix3f_t<br>{<br>&nbsp;&nbsp;&nbsp; struct<br>&nbsp;&nbsp;&nbsp; {<br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; //column major<br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; union { GLfloat M00; GLfloat XX; GLfloat SX; };&nbsp; <br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; union { GLfloat M10; GLfloat XY;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; };&nbsp; <br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; union { GLfloat M20; GLfloat XZ;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; };&nbsp; <br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; union { GLfloat M01; GLfloat YX;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; };&nbsp; <br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; union { GLfloat M11; GLfloat YY; GLfloat SY; };&nbsp; <br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; union { GLfloat M21; GLfloat YZ;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; };&nbsp; <br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; union { GLfloat M02; GLfloat ZX;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; };&nbsp; <br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; union { GLfloat M12; GLfloat ZY;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; };&nbsp; <br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; union { GLfloat M22; GLfloat ZZ; GLfloat SZ; };&nbsp; <br>&nbsp;&nbsp;&nbsp; } s;<br>&nbsp;&nbsp;&nbsp; GLfloat M[9];<br>} Matrix3fT;&nbsp;&nbsp;&nbsp; <br><br>//4x4矩阵<br>typedef union Matrix4f_t<br>{<br>&nbsp;&nbsp;&nbsp; struct<br>&nbsp;&nbsp;&nbsp; {<br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; //column major<br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; union { GLfloat M00; GLfloat XX; GLfloat SX; };&nbsp; <br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; union { GLfloat M10; GLfloat XY;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; };&nbsp; <br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; union { GLfloat M20; GLfloat XZ;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; };&nbsp; <br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; union { GLfloat M30; GLfloat XW;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; };&nbsp; <br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; union { GLfloat M01; GLfloat YX;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; };&nbsp; <br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; union { GLfloat M11; GLfloat YY; GLfloat SY; };&nbsp; <br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; union { GLfloat M21; GLfloat YZ;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; };&nbsp; <br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; union { GLfloat M31; GLfloat YW;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; };&nbsp; <br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; union { GLfloat M02; GLfloat ZX;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; };&nbsp; <br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; union { GLfloat M12; GLfloat ZY;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; };&nbsp; <br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; union { GLfloat M22; GLfloat ZZ; GLfloat SZ; };&nbsp; <br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; union { GLfloat M32; GLfloat ZW;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; };&nbsp; <br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; union { GLfloat M03; GLfloat TX;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; };&nbsp; <br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; union { GLfloat M13; GLfloat TY;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; };&nbsp; <br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; union { GLfloat M23; GLfloat TZ;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; };&nbsp; <br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; union { GLfloat M33; GLfloat TW; GLfloat SW; };&nbsp; <br>&nbsp;&nbsp;&nbsp; } s;<br>&nbsp;&nbsp;&nbsp; GLfloat M[16];<br>} Matrix4fT;&nbsp;&nbsp;&nbsp; <br><br><br>//定义类型的别名<br>#define Point2fT&nbsp;&nbsp;&nbsp; Tuple2fT&nbsp;&nbsp; <br>#define Quat4fT&nbsp;&nbsp;&nbsp;&nbsp; Tuple4fT&nbsp;&nbsp; <br>#define Vector2fT&nbsp;&nbsp; Tuple2fT&nbsp;&nbsp; <br>#define Vector3fT&nbsp;&nbsp; Tuple3fT&nbsp;&nbsp; <br>#define FuncSqrt&nbsp;&nbsp;&nbsp; sqrtf<br># define Epsilon 1.0e-5<br><br>//2维点相加<br>inline<br>static void Point2fAdd(Point2fT* NewObj, const Tuple2fT* t1)<br>{<br>&nbsp;&nbsp;&nbsp; assert(NewObj &amp;&amp; t1);<br><br>&nbsp;&nbsp;&nbsp; NewObj-&gt;s.X += t1-&gt;s.X;<br>&nbsp;&nbsp;&nbsp; NewObj-&gt;s.Y += t1-&gt;s.Y;<br>}<br><br>//2维点相减<br>inline<br>static void Point2fSub(Point2fT* NewObj, const Tuple2fT* t1)<br>{<br>&nbsp;&nbsp;&nbsp; assert(NewObj &amp;&amp; t1);<br><br>&nbsp;&nbsp;&nbsp; NewObj-&gt;s.X -= t1-&gt;s.X;<br>&nbsp;&nbsp;&nbsp; NewObj-&gt;s.Y -= t1-&gt;s.Y;<br>}<br><br>//3维点矢积<br>inline<br>static void Vector3fCross(Vector3fT* NewObj, const Vector3fT* v1, const Vector3fT* v2)<br>{<br>&nbsp;&nbsp;&nbsp; Vector3fT Result; <br><br>&nbsp;&nbsp;&nbsp; assert(NewObj &amp;&amp; v1 &amp;&amp; v2);<br><br>&nbsp;&nbsp;&nbsp; Result.s.X = (v1-&gt;s.Y * v2-&gt;s.Z) - (v1-&gt;s.Z * v2-&gt;s.Y);<br>&nbsp;&nbsp;&nbsp; Result.s.Y = (v1-&gt;s.Z * v2-&gt;s.X) - (v1-&gt;s.X * v2-&gt;s.Z);<br>&nbsp;&nbsp;&nbsp; Result.s.Z = (v1-&gt;s.X * v2-&gt;s.Y) - (v1-&gt;s.Y * v2-&gt;s.X);<br><br>&nbsp;&nbsp;&nbsp; *NewObj = Result;<br>}<br><br>//3维点点积<br>inline<br>static GLfloat Vector3fDot(const Vector3fT* NewObj, const Vector3fT* v1)<br>{<br>&nbsp;&nbsp;&nbsp; assert(NewObj &amp;&amp; v1);<br><br>&nbsp;&nbsp;&nbsp; return&nbsp; (NewObj-&gt;s.X * v1-&gt;s.X) +<br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; (NewObj-&gt;s.Y * v1-&gt;s.Y) +<br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; (NewObj-&gt;s.Z * v1-&gt;s.Z);<br>}<br><br>//3维点的长度的平方<br>inline<br>static GLfloat Vector3fLengthSquared(const Vector3fT* NewObj)<br>{<br>&nbsp;&nbsp;&nbsp; assert(NewObj);<br><br>&nbsp;&nbsp;&nbsp; return&nbsp; (NewObj-&gt;s.X * NewObj-&gt;s.X) +<br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; (NewObj-&gt;s.Y * NewObj-&gt;s.Y) +<br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; (NewObj-&gt;s.Z * NewObj-&gt;s.Z);<br>}<br><br>//3维点的长度<br>inline<br>static GLfloat Vector3fLength(const Vector3fT* NewObj)<br>{<br>&nbsp;&nbsp;&nbsp; assert(NewObj);<br><br>&nbsp;&nbsp;&nbsp; return FuncSqrt(Vector3fLengthSquared(NewObj));<br>}<br><br>//设置3x3矩阵为0矩阵<br>inline<br>static void Matrix3fSetZero(Matrix3fT* NewObj)<br>{<br>&nbsp;&nbsp;&nbsp; NewObj-&gt;s.M00 = NewObj-&gt;s.M01 = NewObj-&gt;s.M02 = <br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; NewObj-&gt;s.M10 = NewObj-&gt;s.M11 = NewObj-&gt;s.M12 = <br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; NewObj-&gt;s.M20 = NewObj-&gt;s.M21 = NewObj-&gt;s.M22 = 0.0f;<br>}<br><br>//设置4x4矩阵为0矩阵<br>inline<br>static void Matrix4fSetZero(Matrix4fT* NewObj)<br>{<br>&nbsp;&nbsp;&nbsp; NewObj-&gt;s.M00 = NewObj-&gt;s.M01 = NewObj-&gt;s.M02 = <br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; NewObj-&gt;s.M10 = NewObj-&gt;s.M11 = NewObj-&gt;s.M12 = <br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; NewObj-&gt;s.M20 = NewObj-&gt;s.M21 = NewObj-&gt;s.M22 = <br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; NewObj-&gt;s.M30 = NewObj-&gt;s.M31 = NewObj-&gt;s.M32 = 0.0f;<br>}<br><br>//设置3x3矩阵为单位矩阵<br>inline<br>static void Matrix3fSetIdentity(Matrix3fT* NewObj)<br>{<br>&nbsp;&nbsp;&nbsp; Matrix3fSetZero(NewObj);<br><br>&nbsp;&nbsp;&nbsp; NewObj-&gt;s.M00 = <br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; NewObj-&gt;s.M11 = <br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; NewObj-&gt;s.M22 = 1.0f;<br>}<br><br>//设置4x4矩阵为单位矩阵<br>inline<br>static void Matrix4fSetIdentity(Matrix4fT* NewObj)<br>{<br>&nbsp;&nbsp;&nbsp; Matrix4fSetZero(NewObj);<br><br>&nbsp;&nbsp;&nbsp; NewObj-&gt;s.M00 = 1.0f;<br>&nbsp;&nbsp;&nbsp; NewObj-&gt;s.M11 = 1.0f;<br>&nbsp;&nbsp;&nbsp; NewObj-&gt;s.M22 = 1.0f;<br>&nbsp;&nbsp;&nbsp; NewObj-&gt;s.M33=1.0f;<br>}<br><br>//从四元数设置旋转矩阵<br>inline<br>static void Matrix3fSetRotationFromQuat4f(Matrix3fT* NewObj, const Quat4fT* q1)<br>{<br>&nbsp;&nbsp;&nbsp; GLfloat n, s;<br>&nbsp;&nbsp;&nbsp; GLfloat xs, ys, zs;<br>&nbsp;&nbsp;&nbsp; GLfloat wx, wy, wz;<br>&nbsp;&nbsp;&nbsp; GLfloat xx, xy, xz;<br>&nbsp;&nbsp;&nbsp; GLfloat yy, yz, zz;<br><br>&nbsp;&nbsp;&nbsp; assert(NewObj &amp;&amp; q1);<br><br>&nbsp;&nbsp;&nbsp; n = (q1-&gt;s.X * q1-&gt;s.X) + (q1-&gt;s.Y * q1-&gt;s.Y) + (q1-&gt;s.Z * q1-&gt;s.Z) + (q1-&gt;s.W * q1-&gt;s.W);<br>&nbsp;&nbsp;&nbsp; s = (n &gt; 0.0f) ? (2.0f / n) : 0.0f;<br><br>&nbsp;&nbsp;&nbsp; xs = q1-&gt;s.X * s;&nbsp; ys = q1-&gt;s.Y * s;&nbsp; zs = q1-&gt;s.Z * s;<br>&nbsp;&nbsp;&nbsp; wx = q1-&gt;s.W * xs; wy = q1-&gt;s.W * ys; wz = q1-&gt;s.W * zs;<br>&nbsp;&nbsp;&nbsp; xx = q1-&gt;s.X * xs; xy = q1-&gt;s.X * ys; xz = q1-&gt;s.X * zs;<br>&nbsp;&nbsp;&nbsp; yy = q1-&gt;s.Y * ys; yz = q1-&gt;s.Y * zs; zz = q1-&gt;s.Z * zs;<br><br>&nbsp;&nbsp;&nbsp; NewObj-&gt;s.XX = 1.0f - (yy + zz); NewObj-&gt;s.YX =&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; xy - wz;&nbsp; NewObj-&gt;s.ZX =&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; xz + wy;<br>&nbsp;&nbsp;&nbsp; NewObj-&gt;s.XY =&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; xy + wz;&nbsp; NewObj-&gt;s.YY = 1.0f - (xx + zz); NewObj-&gt;s.ZY =&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; yz - wx;<br>&nbsp;&nbsp;&nbsp; NewObj-&gt;s.XZ =&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; xz - wy;&nbsp; NewObj-&gt;s.YZ =&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; yz + wx;&nbsp; NewObj-&gt;s.ZZ = 1.0f - (xx + yy);<br>}<br><br>//3x3矩阵相乘<br>inline<br>static void Matrix3fMulMatrix3f(Matrix3fT* NewObj, const Matrix3fT* m1)<br>{<br>&nbsp;&nbsp;&nbsp; Matrix3fT Result; <br><br>&nbsp;&nbsp;&nbsp; assert(NewObj &amp;&amp; m1);<br><br>&nbsp;&nbsp;&nbsp; Result.s.M00 = (NewObj-&gt;s.M00 * m1-&gt;s.M00) + (NewObj-&gt;s.M01 * m1-&gt;s.M10) + (NewObj-&gt;s.M02 * m1-&gt;s.M20);<br>&nbsp;&nbsp;&nbsp; Result.s.M01 = (NewObj-&gt;s.M00 * m1-&gt;s.M01) + (NewObj-&gt;s.M01 * m1-&gt;s.M11) + (NewObj-&gt;s.M02 * m1-&gt;s.M21);<br>&nbsp;&nbsp;&nbsp; Result.s.M02 = (NewObj-&gt;s.M00 * m1-&gt;s.M02) + (NewObj-&gt;s.M01 * m1-&gt;s.M12) + (NewObj-&gt;s.M02 * m1-&gt;s.M22);<br><br>&nbsp;&nbsp;&nbsp; Result.s.M10 = (NewObj-&gt;s.M10 * m1-&gt;s.M00) + (NewObj-&gt;s.M11 * m1-&gt;s.M10) + (NewObj-&gt;s.M12 * m1-&gt;s.M20);<br>&nbsp;&nbsp;&nbsp; Result.s.M11 = (NewObj-&gt;s.M10 * m1-&gt;s.M01) + (NewObj-&gt;s.M11 * m1-&gt;s.M11) + (NewObj-&gt;s.M12 * m1-&gt;s.M21);<br>&nbsp;&nbsp;&nbsp; Result.s.M12 = (NewObj-&gt;s.M10 * m1-&gt;s.M02) + (NewObj-&gt;s.M11 * m1-&gt;s.M12) + (NewObj-&gt;s.M12 * m1-&gt;s.M22);<br><br>&nbsp;&nbsp;&nbsp; Result.s.M20 = (NewObj-&gt;s.M20 * m1-&gt;s.M00) + (NewObj-&gt;s.M21 * m1-&gt;s.M10) + (NewObj-&gt;s.M22 * m1-&gt;s.M20);<br>&nbsp;&nbsp;&nbsp; Result.s.M21 = (NewObj-&gt;s.M20 * m1-&gt;s.M01) + (NewObj-&gt;s.M21 * m1-&gt;s.M11) + (NewObj-&gt;s.M22 * m1-&gt;s.M21);<br>&nbsp;&nbsp;&nbsp; Result.s.M22 = (NewObj-&gt;s.M20 * m1-&gt;s.M02) + (NewObj-&gt;s.M21 * m1-&gt;s.M12) + (NewObj-&gt;s.M22 * m1-&gt;s.M22);<br><br>&nbsp;&nbsp;&nbsp; *NewObj = Result;<br>}<br><br>//4x4矩阵相乘<br>inline<br>static void Matrix4fSetRotationScaleFromMatrix4f(Matrix4fT* NewObj, const Matrix4fT* m1)<br>{<br>&nbsp;&nbsp;&nbsp; assert(NewObj &amp;&amp; m1);<br><br>&nbsp;&nbsp;&nbsp; NewObj-&gt;s.XX = m1-&gt;s.XX; NewObj-&gt;s.YX = m1-&gt;s.YX; NewObj-&gt;s.ZX = m1-&gt;s.ZX;<br>&nbsp;&nbsp;&nbsp; NewObj-&gt;s.XY = m1-&gt;s.XY; NewObj-&gt;s.YY = m1-&gt;s.YY; NewObj-&gt;s.ZY = m1-&gt;s.ZY;<br>&nbsp;&nbsp;&nbsp; NewObj-&gt;s.XZ = m1-&gt;s.XZ; NewObj-&gt;s.YZ = m1-&gt;s.YZ; NewObj-&gt;s.ZZ = m1-&gt;s.ZZ;<br>}<br><br>//进行矩阵的奇异值分解，旋转矩阵被保存到rot3和rot4中，返回矩阵的缩放因子<br>inline<br>static GLfloat Matrix4fSVD(const Matrix4fT* NewObj, Matrix3fT* rot3, Matrix4fT* rot4)<br>{<br>&nbsp;&nbsp;&nbsp; GLfloat s, n;<br><br>&nbsp;&nbsp;&nbsp; assert(NewObj);<br><br>&nbsp;&nbsp;&nbsp; s = FuncSqrt(<br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; ( (NewObj-&gt;s.XX * NewObj-&gt;s.XX) + (NewObj-&gt;s.XY * NewObj-&gt;s.XY) + (NewObj-&gt;s.XZ * NewObj-&gt;s.XZ) + <br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; (NewObj-&gt;s.YX * NewObj-&gt;s.YX) + (NewObj-&gt;s.YY * NewObj-&gt;s.YY) + (NewObj-&gt;s.YZ * NewObj-&gt;s.YZ) +<br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; (NewObj-&gt;s.ZX * NewObj-&gt;s.ZX) + (NewObj-&gt;s.ZY * NewObj-&gt;s.ZY) + (NewObj-&gt;s.ZZ * NewObj-&gt;s.ZZ) ) / 3.0f );<br><br>&nbsp;&nbsp;&nbsp; if (rot3)&nbsp;&nbsp; <br>&nbsp;&nbsp;&nbsp; {<br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; rot3-&gt;s.XX = NewObj-&gt;s.XX; rot3-&gt;s.XY = NewObj-&gt;s.XY; rot3-&gt;s.XZ = NewObj-&gt;s.XZ;<br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; rot3-&gt;s.YX = NewObj-&gt;s.YX; rot3-&gt;s.YY = NewObj-&gt;s.YY; rot3-&gt;s.YZ = NewObj-&gt;s.YZ;<br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; rot3-&gt;s.ZX = NewObj-&gt;s.ZX; rot3-&gt;s.ZY = NewObj-&gt;s.ZY; rot3-&gt;s.ZZ = NewObj-&gt;s.ZZ;<br><br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; n = 1.0f / FuncSqrt( (NewObj-&gt;s.XX * NewObj-&gt;s.XX) +<br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; (NewObj-&gt;s.XY * NewObj-&gt;s.XY) +<br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; (NewObj-&gt;s.XZ * NewObj-&gt;s.XZ) );<br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; rot3-&gt;s.XX *= n;<br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; rot3-&gt;s.XY *= n;<br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; rot3-&gt;s.XZ *= n;<br><br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; n = 1.0f / FuncSqrt( (NewObj-&gt;s.YX * NewObj-&gt;s.YX) +<br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; (NewObj-&gt;s.YY * NewObj-&gt;s.YY) +<br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; (NewObj-&gt;s.YZ * NewObj-&gt;s.YZ) );<br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; rot3-&gt;s.YX *= n;<br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; rot3-&gt;s.YY *= n;<br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; rot3-&gt;s.YZ *= n;<br><br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; n = 1.0f / FuncSqrt( (NewObj-&gt;s.ZX * NewObj-&gt;s.ZX) +<br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; (NewObj-&gt;s.ZY * NewObj-&gt;s.ZY) +<br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; (NewObj-&gt;s.ZZ * NewObj-&gt;s.ZZ) );<br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; rot3-&gt;s.ZX *= n;<br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; rot3-&gt;s.ZY *= n;<br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; rot3-&gt;s.ZZ *= n;<br>&nbsp;&nbsp;&nbsp; }<br><br>&nbsp;&nbsp;&nbsp; if (rot4)&nbsp; <br>&nbsp;&nbsp;&nbsp; {<br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; if (rot4 != NewObj)<br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; {<br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; Matrix4fSetRotationScaleFromMatrix4f(rot4, NewObj); <br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; }<br><br><br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; n = 1.0f / FuncSqrt( (NewObj-&gt;s.XX * NewObj-&gt;s.XX) +<br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; (NewObj-&gt;s.XY * NewObj-&gt;s.XY) +<br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; (NewObj-&gt;s.XZ * NewObj-&gt;s.XZ) );<br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; rot4-&gt;s.XX *= n;<br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; rot4-&gt;s.XY *= n;<br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; rot4-&gt;s.XZ *= n;<br><br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; n = 1.0f / FuncSqrt( (NewObj-&gt;s.YX * NewObj-&gt;s.YX) +<br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; (NewObj-&gt;s.YY * NewObj-&gt;s.YY) +<br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; (NewObj-&gt;s.YZ * NewObj-&gt;s.YZ) );<br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; rot4-&gt;s.YX *= n;<br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; rot4-&gt;s.YY *= n;<br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; rot4-&gt;s.YZ *= n;<br><br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; n = 1.0f / FuncSqrt( (NewObj-&gt;s.ZX * NewObj-&gt;s.ZX) +<br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; (NewObj-&gt;s.ZY * NewObj-&gt;s.ZY) +<br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; (NewObj-&gt;s.ZZ * NewObj-&gt;s.ZZ) );<br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; rot4-&gt;s.ZX *= n;<br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; rot4-&gt;s.ZY *= n;<br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; rot4-&gt;s.ZZ *= n;<br>&nbsp;&nbsp;&nbsp; }<br><br>&nbsp;&nbsp;&nbsp; return s;<br>}<br><br>//从3x3矩阵变为4x4的旋转矩阵<br>inline<br>static void Matrix4fSetRotationScaleFromMatrix3f(Matrix4fT* NewObj, const Matrix3fT* m1)<br>{<br>&nbsp;&nbsp;&nbsp; assert(NewObj &amp;&amp; m1);<br><br>&nbsp;&nbsp;&nbsp; NewObj-&gt;s.XX = m1-&gt;s.XX; NewObj-&gt;s.YX = m1-&gt;s.YX; NewObj-&gt;s.ZX = m1-&gt;s.ZX;<br>&nbsp;&nbsp;&nbsp; NewObj-&gt;s.XY = m1-&gt;s.XY; NewObj-&gt;s.YY = m1-&gt;s.YY; NewObj-&gt;s.ZY = m1-&gt;s.ZY;<br>&nbsp;&nbsp;&nbsp; NewObj-&gt;s.XZ = m1-&gt;s.XZ; NewObj-&gt;s.YZ = m1-&gt;s.YZ; NewObj-&gt;s.ZZ = m1-&gt;s.ZZ;<br>}<br><br>//4x4矩阵的与标量的乘积<br>inline<br>static void Matrix4fMulRotationScale(Matrix4fT* NewObj, GLfloat scale)<br>{<br>&nbsp;&nbsp;&nbsp; assert(NewObj);<br><br>&nbsp;&nbsp;&nbsp; NewObj-&gt;s.XX *= scale; NewObj-&gt;s.YX *= scale; NewObj-&gt;s.ZX *= scale;<br>&nbsp;&nbsp;&nbsp; NewObj-&gt;s.XY *= scale; NewObj-&gt;s.YY *= scale; NewObj-&gt;s.ZY *= scale;<br>&nbsp;&nbsp;&nbsp; NewObj-&gt;s.XZ *= scale; NewObj-&gt;s.YZ *= scale; NewObj-&gt;s.ZZ *= scale;<br>}<br><br>//设置旋转矩阵<br>inline<br>static void Matrix4fSetRotationFromMatrix3f(Matrix4fT* NewObj, const Matrix3fT* m1)<br>{<br>&nbsp;&nbsp;&nbsp; GLfloat scale;<br><br>&nbsp;&nbsp;&nbsp; assert(NewObj &amp;&amp; m1);<br><br>&nbsp;&nbsp;&nbsp; scale = Matrix4fSVD(NewObj, NULL, NULL);<br><br>&nbsp;&nbsp;&nbsp; Matrix4fSetRotationScaleFromMatrix3f(NewObj, m1);<br>&nbsp;&nbsp;&nbsp; Matrix4fMulRotationScale(NewObj, scale);<br>}<br><br><br><br>typedef class ArcBall_t<br>{<br>protected:<br>&nbsp;&nbsp;&nbsp; //把二维点映射到三维点<br>&nbsp;&nbsp;&nbsp; inline<br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; void _mapToSphere(const Point2fT* NewPt, Vector3fT* NewVec) const;<br><br>public:<br>&nbsp;&nbsp;&nbsp; //构造/析构函数<br>&nbsp;&nbsp;&nbsp; ArcBall_t(GLfloat NewWidth, GLfloat NewHeight);<br>&nbsp;&nbsp;&nbsp; ~ArcBall_t() { };<br><br>&nbsp;&nbsp;&nbsp; //设置边界<br>&nbsp;&nbsp;&nbsp; inline<br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; void&nbsp;&nbsp;&nbsp; setBounds(GLfloat NewWidth, GLfloat NewHeight)<br>&nbsp;&nbsp;&nbsp; {<br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; assert((NewWidth &gt; 1.0f) &amp;&amp; (NewHeight &gt; 1.0f));<br><br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; //设置长宽的调整因子<br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; this-&gt;AdjustWidth&nbsp; = 1.0f / ((NewWidth&nbsp; - 1.0f) * 0.5f);<br>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; this-&gt;AdjustHeight = 1.0f / ((NewHeight - 1.0f) * 0.5f);<br>&nbsp;&nbsp;&nbsp; }<br><br>&nbsp;&nbsp;&nbsp; //鼠标点击<br>&nbsp;&nbsp;&nbsp; void&nbsp;&nbsp;&nbsp; click(const Point2fT* NewPt);<br><br>&nbsp;&nbsp;&nbsp; //鼠标拖动计算旋转<br>&nbsp;&nbsp;&nbsp; void&nbsp;&nbsp;&nbsp; drag(const Point2fT* NewPt, Quat4fT* NewRot);<br><br>&nbsp;&nbsp;&nbsp; //更新鼠标状态<br>&nbsp;&nbsp;&nbsp; void&nbsp;&nbsp;&nbsp; upstate();<br>&nbsp;&nbsp;&nbsp; //void&nbsp;&nbsp;&nbsp; mousemove(WPARAM wParam,LPARAM lParam);<br><br>protected:<br>&nbsp;&nbsp;&nbsp; Vector3fT&nbsp;&nbsp; StVec;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; //保存鼠标点击的坐标<br>&nbsp;&nbsp;&nbsp; Vector3fT&nbsp;&nbsp; EnVec;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; //保存鼠标拖动的坐标<br>&nbsp;&nbsp;&nbsp; GLfloat&nbsp;&nbsp;&nbsp;&nbsp; AdjustWidth;&nbsp;&nbsp;&nbsp; //宽度的调整因子<br>&nbsp;&nbsp;&nbsp; GLfloat&nbsp;&nbsp;&nbsp;&nbsp; AdjustHeight;&nbsp;&nbsp; //长度的调整因子<br>public:<br>&nbsp;&nbsp;&nbsp; Matrix4fT&nbsp;&nbsp; Transform;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; //计算变换&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; <br>&nbsp;&nbsp;&nbsp; Matrix3fT&nbsp;&nbsp; LastRot;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; //上一次的旋转 <br>&nbsp;&nbsp;&nbsp; Matrix3fT&nbsp;&nbsp; ThisRot;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; //这次的旋转<br>&nbsp;&nbsp;&nbsp; float zoomRate;<br>&nbsp;&nbsp;&nbsp; float lastZoomRate;<br><br>&nbsp;&nbsp;&nbsp; bool&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; isDragging;&nbsp;&nbsp;&nbsp;&nbsp; // 是否拖动<br>&nbsp;&nbsp;&nbsp; bool&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; isRClicked;&nbsp;&nbsp;&nbsp;&nbsp; // 是否右击鼠标<br>&nbsp;&nbsp;&nbsp; bool&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; isClicked;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; // 是否点击鼠标<br>&nbsp;&nbsp;&nbsp; bool&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; isZooming;&nbsp;&nbsp;&nbsp; //是否正在缩放<br>&nbsp;&nbsp;&nbsp; Point2fT&nbsp;&nbsp;&nbsp; LastPt;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; <br>&nbsp;&nbsp;&nbsp; Matrix4fT origTransform;<br>&nbsp;&nbsp;&nbsp; Point2fT&nbsp;&nbsp;&nbsp; MousePt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; // 当前的鼠标位置<br><br>} ArcBallT;<br><br>#endif<br>

1. 搭建mirror<br>1.1 首先安装启动apache服务器 <br>sudo apt-get install apache2<br>1.2 挂载安装文件镜像<br>sudo mkdir /var/www/ubuntu<br>sudo mount -o loop ubuntu-server-8.04.2-i386.iso /var/www/ubuntu<br><br>2. 搭建PXE服务器<br>2.1 搭建dhcp服务器<br>sudo apt-get install dhcp3-server<br>sudo vi /etc/default/dhcp3-server<br>INTERFACES="<span style="color: rgb(255, 0, 0);">eth0</span>"<br>sudo vi /etc/dhcp3/dhcpd.conf<br><style>BODY { FONT-FAMILY:Tahoma; FONT-SIZE:10pt }
